<template>
    <div>
        <h2>Login</h2>
        <v-form @submit.prevent="handleSubmit">
            <v-text-field
                v-model="username"
                label="Username"
                placeholder="PDM Username"
                required
                outlined
                ></v-text-field>
            <v-text-field
                v-model="password"
                type="password"
                label="Password"
                placeholder="PDM Password"
                required
                outlined
                ></v-text-field>

            
            <div class="form-group">
                <button class="btn btn-primary" :disabled="loggingIn">Login</button>
                <img v-show="loggingIn" src="data:image/gif;base64," />
            </div>
        </v-form>
    </div>
</template>

<script>
export default {
    data () {
        return {
            username: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        loggingIn () {
            return this.$store.state.authentication.status.loggingIn;
        }
    },
    created () {
        
    },
    methods: {
        // eslint-disable-next-line
        handleSubmit (e) {
            this.submitted = true;
            const { username, password } = this;
            const { dispatch } = this.$store;

            if (username && password) {
                dispatch('authentication/login', { username, password });
            }
        }
    }
};
</script>